import logo from './logo.svg';
import './App.css';
import FormInput from './components/FormInput';

function App() {


  const handleSubmit = (e)=>{
    e.preventDefault();
    const data= new FormData(e.target)
  };

  return (
    <div className="App">
      <h1>Firm name</h1>
      <br></br>
      <h2>Yoga classes registration form</h2>
      <p>Kindly fill out the form for joining the Yoga classes at xyz.The monthly fees for the sessions at
        xyz is INR 500,you can pay any day of the month for taking up the classes for that entire month.You
        need to be in the age range of 18-65 years and the payment is done on a monthly basis.There are a 
        total of 4 available batches for you namely 6-7 AM, 7-8 AM, 8-9 AM and 5-6 PM,you can choose any of 
        them for a particular month.</p>
      <br></br>

      <form onSubmit={handleSubmit}>
      <h4>Enter your name:</h4>
      <FormInput  name="fullName" placeholder="Full Name" />
      <h4>Enter your email-id:</h4>
      <FormInput name="email" placeholder="Email ID"/>
      <h4>Enter your phone number:</h4>
      <FormInput name="fullName" placeholder="Phone Number"/>
      <br></br>
      <button>Submit</button>
      </form>
    </div>
  );
}

export default App;
